Sa=3.14*r*r;  
Sb=4*Sa;  
Va=4*3.14*r*r*r/3;  
Vb=Sa*h;  
printf("C1=%.2f\n",C1);  
printf("Sa=%.2f\nSb=%.2f\nVa=%.2f\nVb=%.2f\n",Sa,Sb,Va,Vb);  
}

