#include <stdio.h>
int main()
{
    int a;
    char v;
    scanf("%d",&a);
    if(a>=90) v='A';
    else
    {
        if(a>=80&&a<90) v='B';
        else
        {
            if(a>=70&&a<80) v='C';
            else
            {
                if(a>=60&&a<70) v='D';
                else v='E';
            }
        }
    }
    printf("%c",v);
}

#include<stdio.h>
char f(int x){
  if(x<60) return 'E';
  if(x>=60&&x<70) return 'D';
  if(x>=70&&x<80) return 'C';
  if(x>=80&&x<90) return 'B';
  return 'A';
}
