#include <stdio.h>
#include <string.h>
int main(){
  char a[10];
  int i,l;
  scanf("%s",a);
  l=strlen(a);
  printf("%d\n",l);
  for(i=0;i<l-1;i++)
  printf("%c ",a[i]);
  printf("%c\n",a[i]);
  for(i=l-1;i>=0;i--)
  printf("%c",a[i]);


  return 0;
}

#include <stdio.h>

int main(int argc, const char * argv[])
{
    
    int i,j,m,n,a,b,max;
    scanf("%d %d",&m,&n);
    if(m>n)
    {
        max=m;
    }
    else max=n;
    for(i=max;i>=1;i--)
    {
        a=m%i;
        b=n%i;
        if(a==0&&b==0)
        {
            printf("%d ",i);
            break;
        }
        
    }
    for(j=1;j<=max;j++)
    {
        a=m*j;
        b=a%n;
        if(b==0)
        {
            printf("%d\n",a);
            break;
        }
    }
}

#include<stdio.h>
int main()
{
	int p,r,n,m,temp;
	scanf("%d %d",&m,&n);
	if(n<m)
	{
		temp=n;
		n=m;
		m=temp;
	}
	p=n*m;
	while(m!=0)
	{
		r=n%m;
		n=m;
		m=r;
	}
	printf("%d %d",n,p/n);
return 0;
}

