#include <stdio.h>

int main()
{
    
    int i,j,a,b,c,sa=0,sb=0,j1;
    float k,sc=0,k1,s;
    scanf("%d %d %d",&a,&b,&c);
    for(i=1;i<=a;i++)
    {
        sa=sa+i;
    }
    for(j=1;j<=b;j++)
    {
        j1=j*j;
        sb=sb+j1;
    }
    for(k=1;k<=c;k++)
    {
        k1=1/k;
        sc=sc+k1;
    }
    s=sa+sb+sc;
    printf("%.2f\n",s);
}

