}

#include <stdio.h>

int main()
{
    int i,j,sum1,sum2,a[3][3];
    for(i=0;i<3;i++)
    {
        for(j=0;j<3;j++)
        {
            scanf("%d",&a[i][j]);
        }
    }
    sum1=a[0][0]+a[1][1]+a[2][2];
    sum2=a[0][2]+a[1][1]+a[2][0];
    printf("%d %d",sum1,sum2);
}

#include <stdio.h>
#define ARRLEN 3

int main(void){
  int n[ARRLEN],max,i=0;
  while(i<ARRLEN){
     scanf("%d",n+i);
     i++;
  }
  max = n[0];
  for(int j=1;j<ARRLEN;j++){
      if(max<n[j]){
         max = n[j];
      }
  }
  printf("%d\n",max);
  
  return 0;
}

#include <stdio.h>

int main()
{
    int i,j,a[10],b[10];
    for(i=0;i<10;i++)
    {
        scanf("%d",&a[i]);
        j=9-i;
        b[j]=a[i];
    }
    for(j=0;j<=9;j++)
    {
        if(j==9) printf("%d\n",b[j]);
        else
        {
            printf("%d ",b[j]);
            continue;
        }
    }
}

#include <stdio.h>
#include <string.h>
int main(){	
	int iInput = 0;
	int aInput[10] = {0};
	int i = 0;
	while(scanf(" %d", &iInput) != EOF)
	{
		aInput[i++] = iInput;
		if (i == 10)
		{
			break;
		}
	}
	i--;
	for(; i != 0; --i)
	{
		printf("%d ",aInput[i]);
	}
	printf("%d",aInput[i]);
	return 0;
}

