psushu(m)  
int m;  
{
	int i=2,t;  
	for(;i<=m;i++)  
		if(m%i==0&&i<m) break;  
	if(i==m+1) t=1;  
		else t=0;  
	return t;  
}  
main()  
{int a,s;  
//printf("enter sushu is \n");  
scanf("%d",&a);  
s=psushu(a);  
if(s==1) printf("prime");  
else printf("not prime");  
}

#include <stdio.h>
void main(void)
{
	int a = 0, b = 0, c = 0;
	scanf("%d %d",&a,&b);
	c = a ^ b;
	a ^= c;
	b ^= c;
	printf("%d\n%d\n",a,b);
}

int zhuangzhi(b)  
int b[3][3];  
{int i,j,t;  
for(i=0;i<3;i++)  
for(j=0;j<=i;j++)  
{t=b[i][j];b[i][j]=b[j][i];b[j][i]=t;}  
}  
int main()  
{int a[3][3];int i,j;  
for(i=0;i<3;i++)  
for(j=0;j<3;j++)  
scanf("%d",&a[i][j]);  

zhuangzhi(a);  
for(i=0;i<3;i++)  
{for(j=0;j<3;j++)  
printf("%d ",a[i][j]);  
printf("\n");}  
return 0;
}

