#include<stdio.h>
int main()
{
    int i=0,n,m,a[32];

    scanf("%d",&n);
    while (n>0)
    {
        a[i]=n%2;
        i=i+1;
        n=n/2;
    }
    m=i;
  
    for(i=31;i >= 0;i--)
       {
           if (i >= m)
               printf("0");
           
           else
        printf("%d",a[i]);
    }
    printf("\n");
}

#include"math.h"  
#define SSS(m,n,k) (m+n+k)/2  
#define AQRT(m,n,k) sqrt(SSS(m,n,k)*(SSS(m,n,k)-m)*(SSS(m,n,k)-n)*(SSS(m,n,k)-k))  
main()  
{float a,b,c,s,area;  
scanf("%f%f%f",&a,&b,&c);  
s=SSS(a,b,c);  
area=AQRT(a,b,c);  
printf("%.3f\n",area);  
}

char tongji(str0,b)
char str0[100];
int b[4];
{int i;
for(i=0;str0[i]!='\0';i++)
{if(str0[i]>=65&&str0[i]<=90||str0[i]>=97&&str0[i]<=122) b[0]++;
else if(str0[i]>=48&&str0[i]<=57) b[1]++;
else if(str0[i]==32) b[2]++;
else b[3]++;}
}
