int main()
{
  int i = 0,j = 0,k = 0;
  int a = 0;
  scanf("%d",&a);
  for(i = 0;i <= a;i++)
  {
   for(j = 0;j < 2*i;j++)
   {
     printf(" ");
   }
   for(k = 0;k < 8;k++)
   {
    printf("#");
   }
   printf("\n");
  }

  return 0;
}

#include <stdio.h>

int main(void){
    int n = 0;
    scanf("%d",&n);
    for(int i=0;i<=n;i++){
