}

#include <stdio.h>
int main(void)
{
	int i,j,k;               /*i为行数，j为空格数，k位“*”数*/
	scanf("%d",&i);
	for(int a=1;a<=i;a++)    /* "*"为i*2-1, "空格为5-i"  */
	{
		for(j=1;j<=i-a;j++)
		{
			printf(" ");
		}
		for(k=1;k<=(a*2-1);k++)
		{
			printf("*");
		}
		printf("\n");
	}
	for(int a=1;a<i;a++)    /*  "*"为*/
	{
		for(j=1;j<=a;j++)
		{
			printf(" ");
		}
		for(k=1;k<=(i*2-1-a*2);k++)
		{
			printf("*");
		}
		printf("\n");
	}              
	
}

#include <stdio.h>

int main(void)
{
    long int i,j,n,m,k,g,s,h,a[100][2] = {0};
    scanf("%ld",&n);
    for (i = 0; i < n; i++)
    {
        for (j = 0; j < 2; j++)
        {
            scanf("%ld",&a[i][j]);
        }
    }
    i = 0;
    for (h = 0; h < n; h++)
    {
        
        for (g = 1; g <= a[i][1]; g++)
        {
            
            if (g == a[i][1])
            {
                s = 0;
                for (k = 0; k < a[i][0]; k++)
                {
                    for (m = 0; m < a[i][0]; m++)
                    {
                        if (m == s || m == a[i][0]-s-1)
                        {
                            printf("X");
                        }
                        else
                        {
                            printf(" ");
                        }
                        if((s<=a[i][0]/2&&m==a[i][0]-s-1)||(s>a[i][0]/2&&m==s))
                        {
                            break;
                        }
                    }
                    printf("\n");
                    s++;
                }
                
                
            }
            
            else
            {
                s = 0;
                for (k = 0; k < a[i][0]-1; k++)
                {
                    for (m = 0; m < a[i][0]; m++)
                    {
                        if (m == s || m == a[i][0]-s-1)
                        {
                            printf("X");
                        }
                        else
                        {
                            printf(" ");
                        }
                        if((s<=a[i][0]/2&&m==a[i][0]-s-1)||(s>a[i][0]/2&&m==s))
                        {
                            break;
                        }
                        
                    }
                    printf("\n");
                    s++;
                }
                
            }
            
        }
        
        printf("\n");
        i++;
    }
    
    return 0;
}

