#include <stdio.h>

int main(int argc, const char * argv[])
{
    int i=0;
    int j=0;//i 行  j列
    
    do
    {
        j = 1;//每次都让从1*N = N
        do
        {
            printf("\t%2d*%2d=%2d",j,i,j*i);//1*3=3 2*3=6 3*3=9
            j++;
        } while (j<=i);//列
        printf("\n");
        i++;//行+1 向下移动  1*4=4  ....    4*4=16
    } while (i<10);//行
    
    
    return 0;
}

#include <stdio.h>
//#include <conio.h>

int main(void)
    {
        char c, init;
        int i, j;
        //printf("请输入一个字母: ");
        scanf("%c", &c);
        if ((c >= 'a') && (c <= 'z'))
        {
            init = 'a';
        }
        else if ((c >= 'A') && (c <= 'Z'))
        {
            init = 'A';
        }
        else
        {
            printf("输入错误");
            //getch();
            return 0;
        }
        for (i = init; i <= c; i++)
        {
            for (j = 0; j < c - i; j++)
            {
                printf(" ");
            }
            for (j = init; j <= i; j++)
            {
                printf("%c", j);
            }
            for (j = i - 1; j >= init; j--)
            {
                printf("%c", j);
            }
            printf("\n");
        }
        //getch();
        return 0;
    }

#include <stdio.h>

int main(int argc, const char * argv[])
{

    int i = 0,j = 0;
    
    do
    {
        j = 0;
        do
        {
            (j < 4 - i || j > 4 + i)?printf(" "):printf("*");
            j++;
        } while (j < 10);
        printf("\n");
        i++;
    } while (i < 5);
    
    
    
    return 0;
}

