int main(int argc, const char * argv[]) {
    int m,n;
    int a=0;
    int b=0;
    int i=0;
    
    scanf("%d",&m);
    scanf("%d",&n);
    for ( i=1; i<m; i++) {
        if (m%i==0&&n%i==0) {
            a=i;
        }
    }
    for ( i=m;i%m!=0||i%n!=0; i++) {
        b=i+1;
    }
    printf("%d %d",a,b);
    return 0;
}

