#include <stdio.h>
void prim(int n){
	int i,j,a[n];  
	for(i=2;i<n;i++)  
	{ 	a[i]=i;  
		for(j=2;j<=i;j++)  
		{
			if(j<i)  
			 if(a[i]%j==0)  
			 break;  
			if(a[i]-j==0)  
			 printf("%d\n",a[i]);  
		}  
	} 	
}
main()  
{  
	int  a;
	scanf("%d",&a);
	prim(a);
}

