int main()
{
	int i,a,b,c;
	for (int i = 100; i <1000; i++)
	{
		a = i / 100;
		b = i / 10 % 10;
		c = i % 10;

		if (i==(a*a*a+b*b*b+c*c*c))
		{
			printf("%d\n", i);
		}

	}
	
}

#include <stdio.h>
float f(int n)
{
    float b;
    if (n==1) {
        b=2.0;
    }
    if (n==2) {
        b=3.0;
    }
    else if(n!=1&&n!=2)
    {  b=f(n-2)+f(n-1);}
    
    
    return b;

}
int main(int argc, const char * argv[]) {
    int n;
    float sum=2.0;
    scanf("%d",&n);
    for (int i=2; i<=n; i++) {
        sum=sum+f(i)/f(i-1);
    }
    printf("%.2f",sum);
    
    return 0;
}

