int main(){	
	int iInput = 0;
	int aInput[10] = {0};
	int i = 0;
	while(scanf(" %d", &iInput) != EOF)
	{
		aInput[i++] = iInput;
		if (i == 10)
		{
			break;
		}
	}
	i--;
	for(; i != 0; --i)
	{
		printf("%d ",aInput[i]);
	}
	printf("%d",aInput[i]);
	return 0;
}

