#include<stdio.h>
int maxyueshu(int m,int n)  

{  int i=1,t;  
for(;i<=m&&i<=n;i++)  
{if(m%i==0&&n%i==0)  
 t=i;  
}  
return(t);  
}  
int minbeishu(int m,int n)  

{int j;  
if(m>=n) j=m;  
else j=n;  
for(;!(j%m==0&&j%n==0);j++);  
return j;  
}  
int main()  
{int a,b,max,min;  
//printf("enter two number is: ");  
scanf("%d%d",&a,&b);  
max=maxyueshu(a,b);  
min=minbeishu(a,b);  
printf("%d %d",max,min);  
return 0;
}

