float yishigen(m,n,k)  
float m,n,k;  
{float x1,x2;  
x1=(-n+sqrt(k))/(2*m);  
x2=(-n-sqrt(k))/(2*m);  
printf("x1=%.3f x2=%.3f\n",x1,x2);  
}  
