float m,n;  
{float x;  
x=-n/(2*m);  
printf("x1=%.3f x2=%.3f\n",x,x);  
}  
float xugen(m,n,k)  
float m,n,k;  
{float x,y;  
x=-n/(2*m);  
y=sqrt(-k)/(2*m);  
printf("x1=%.3f+%.3fi x2=%.3f-%.3fi\n",x,y,x,y);  
}  
