{float a,b,c,q;  
//printf("input a b c is ");  
scanf("%f%f%f",&a,&b,&c);  
//printf("\n");  
q=b*b-4*a*c;  
if(q>0) yishigen(a,b,q);  
else if(q==0) denggen(a,b);  
else xugen(a,b,q);  
}

#include <stdio.h>
main()  
{int i=1,sum=1;  
int n;
scanf("%d",&n);
for(;i<n;sum=2*(sum+1),i++);  
printf("%d\n",sum);  
}

int zhuangzhi(b)  
int b[3][3];  
{int i,j,t;  
for(i=0;i<3;i++)  
for(j=0;j<=i;j++)  
{t=b[i][j];b[i][j]=b[j][i];b[j][i]=t;}  
}  
int main()  
{int a[3][3];int i,j;  
for(i=0;i<3;i++)  
for(j=0;j<3;j++)  
scanf("%d",&a[i][j]);  

zhuangzhi(a);  
for(i=0;i<3;i++)  
{for(j=0;j<3;j++)  
printf("%d ",a[i][j]);  
printf("\n");}  
return 0;
}

fanxu(str1)  
char str1[100];  
{int i,t,j;  
char str2[100];strcpy(str2,str1);  
t=strlen(str1);  
for(i=0,j=t-1;j>-1;i++,j--)  
str1[i]=str2[j];
str1[i--]=0;
}  

int main()  
{char str0[100];  
scanf("%s",str0);  
fanxu(str0);  
printf("%s",str0);  
 return 0;
}

#include<stdio.h>
main()  
{ int i,j,a[10],t;  
for(i=0;i<10;i++)  
scanf("%d",&a[i]);  
for(j=1;j<10;j++)  
for(i=0;i<=9-j;i++)  
if(a[i]>a[i+1])   
{t=a[i+1];a[i+1]=a[i];a[i]=t;}  
for(i=0;i<10;i++)  
printf("%d\n",a[i]);  
}

