#include<stdio.h>
int f(int x){
  if(x<1) return x;
  if(x>=1&&x<10) return x*2-1;
  return x*3-11;
}
int main(){
  int a;
  scanf("%d",&a);
  printf("%d",f(a));
  return 0;
}

