scanf("%d",&y);  
if(LEAPYEAR(y)) printf("L");  
else printf("N");  
}

#define PR1(x) printf("%6.2f\n",x)  
#define PR2(x) printf("%6.2f %6.2f\n",x,x)  
#define PR3(x) printf("%6.2f %6.2f %6.2f\n",x,x,x)  
main()  
{float a;scanf("%f",&a);  
PR1(a);PR2(a);PR3(a);  
}

#define MAX(a,b,c) (a>((b>c)? b:c))? a:((b>c)? b:c)  
main()  
{float x,y,z,t;  
scanf("%f%f%f",&x,&y,&z);  
t=MAX(x,y,z);  
printf("%.3f\n",t);  
printf("%.3f",t);  
}

