#include<stdio.h>
#include<string.h>

void Swap(char **q1, char**q2);

int main()
{    
    int i=0;
    char *p1,*p2,*p3, str1[80],str2[80],str3[80];
 p1=str1; p2=str2; p3=str3;
    gets(str1);
	gets(str2);
	gets(str3);
if (strcmp(p1, p2)>0)
        Swap(&p1, &p2);  
if (strcmp(p1, p3)>0)
        Swap(&p1, &p3);
if (strcmp(p2, p3)>0)
        Swap(&p2, &p3);
 printf("%s\n%s\n%s\n",p1,p2,p3);
 return(0);
}

void Swap(char **q1, char* *q2)
{char *t;
 t= *q1;
 *q1 = *q2;
 *q2 = t;
}

