#include<stdio.h>
int main()
{
	int array[11];
	int n=10;
	//scanf("%d",&n);
	int temp;
	int i;
	for(i=0;i<n;i++)
		scanf("%d",&array[i]);
	temp=0;
	for(i=0;i<n;i++)
		if(array[temp] > array[i])
			temp = i;
		int t = array[temp];
		array[temp] = array[0];
		array[0] = t;
	temp = 0;
	for(i=0;i<n;i++)
		if(array[temp] < array[i])
			temp = i;
		t = array[temp];
		array[temp] = array[n-1];
		array[n-1] = t;
	for(i=0;i<n;i++)
	{

		printf("%d ",array[i]);
	}
	printf("\n");
	return 0;
}

#include"math.h"  
#define SSS(m,n,k) (m+n+k)/2  
