#include <stdio.h>
void mv(int *p,int n,int m)
{
    int *h, t,i;
    h=p+n-m-1; 
    for(;h>=p;h--)
    {
         for(i=0;i<m;i++) 
         {
                     t=*(h+i);
                     *(h+i)=*(h+i+1);
                     *(h+i+1)=t;
          }
   }
}


int main()
{
    int a[80];
     int m,i,n;
	 scanf("%d",&n);
 for(i=0;i<n;i++) 
	 scanf("%d",&a[i]);
   scanf("%d",&m);
  mv(a,n,m); 
 for(i=0;i<n;i++) 
	 printf("%d ",a[i]);

  return 0;
}

