#include <stdio.h> 

const int M = 3; 

int main() 
{ 
    int n, s = 0; 
    scanf("%d", &n); 
    for (int i = 2; i <= n; ++i) 
        s = (s+M)%i; 
    printf("%d\n", s+1); 
    return 0; 
}

struct  
{int year;  
 int month;  
 int day;  
}date;  
main()  
{int days;
//printf("Input  year,month,day:");
scanf("%d%d%d",&date.year,&date.month,&date.day);
switch(date.month)  
 {case 1: days=date.day;    break;  
 case 2: days=date.day+31; break;  
 case 3: days=date.day+59; break;  
 case 4: days=date.day+90; break;  
 case 5: days=date.day+120;break;  
case 6: days=date.day+31; break;  
   case 7: days=date.day+181; break;  
   case 8: days=date.day+212; break;  
case 9: days=date.day+243; break;  
case 10: days=date.day+273; break;  
case 11: days=date.day+304; break;  
case 12: days=date.day+334; break;  
}  
if((date.year%4==0&&date.year%100!=0||date.year%400==0)&&date.month>=3)days+=1;  
printf("%d",days);  
}

#include <stdio.h>
#define max(a,b,c) {if(a<b) a=b;if(a<c) a=c;}
int main()
{
    float a,b,c,max;
    scanf("%f%f%f",&a,&b,&c);
    max(a,b,c);
    printf("%.3f\n",a);
    
    max = a;
    if(b>a)
        max = b;
    if(c>a)
        max = c;
    printf("%.3f\n",max);
}

int N=100;
struct student  
{char num[6];  
   char name[8];  
   int score[3];  
}stu[100]; 
void print(struct student st)  
{	 int i,j;  
     printf("%s,%s",st.num,st.name);  
     for(j=0;j<3;j++)  
     printf(",%d",st.score[j]);  
     printf("\n");  
} 
main()  
{int i,j ;  
   scanf ("%d",&N);
   for(i=0;i<N;i++)  
{
	//printf("Input score of student %d:\n",i+1);  
     //printf("no.:");  
     scanf("%s",stu[i].num);  
     ///printf("name:");  
     scanf("%s",stu[i].name);  
     for(j=0;j<3;j++)  
     {//printf("score%d:",j+1);  
      scanf("%d",&stu[i].score[j]);  
}  

 print(stu[i]);  
}  


}

#include<stdio.h>
#include<string.h>
void main()
{
    char str1[50],str2[50],str3[50];
    void swap(char *,char *);
    scanf("%s",str1);
    scanf("%s",str2);
    scanf("%s",str3);
    if(strcmp(str1,str2)>0)swap(str1,str2);
    if(strcmp(str1,str3)>0)swap(str1,str3);
    if(strcmp(str2,str3)>0)swap(str2,str3);
    printf("%s\n%s\n%s\n",str1,str2,str3);
    
    return 0;
}
   
void swap(char *p1,char *p2)
{
     char p[50];
     strcpy(p,p1);
     strcpy(p1,p2);
     strcpy(p2,p);
}

