#include<stdio.h>
int main()
{
	int array[11];
	int n=10;
	//scanf("%d",&n);
	int temp;
	int i;
	for(i=0;i<n;i++)
		scanf("%d",&array[i]);
	temp=0;
	for(i=0;i<n;i++)
		if(array[temp] > array[i])
			temp = i;
		int t = array[temp];
		array[temp] = array[0];
		array[0] = t;
	temp = 0;
	for(i=0;i<n;i++)
		if(array[temp] < array[i])
			temp = i;
		t = array[temp];
		array[temp] = array[n-1];
		array[n-1] = t;
	for(i=0;i<n;i++)
	{

		printf("%d ",array[i]);
	}
	printf("\n");
	return 0;
}

#include <stdio.h>
void change(int n,int b[],int a[])
{   int j=0;
    for (int i=0; i<n; i++) {
        if (a[i]!=0) {
            b[j]=a[i];
            j++;
        };
    }
}
int main(int argc, const char * argv[])
{   int m;
    int n;
    int z;
  //  printf("输入n");
    scanf("%d",&n);
    int a[n];
    int b[n];
    for (int i=0; i<n; i++)
    {
        a[i]=i+1;
    }
    z=n;
    int j=0;
    for (j=0; j<z; j++)
    {
        for (int i=0; i<n; i++)
        {
            if ((i+1)%3==0)
            {
                a[i]=0;
            }
        }
        change(n,b, a);
        m=n;
        n=n-n/3;
        if (m%3==1)
        {
            for (int i=0; i<n; i++)
            {   a[i+1]=b[i];
            }
            a[0]=b[n-1];
               }
        if (m%3==0)
        {
            for (int i=0; i<n; i++)
            {   a[i]=b[i];
            }
          
        }
        
        if (m%3==2)
        {
            for (int i=0; i<n; i++)
            {   a[i+2]=b[i];
            }
            a[0]=b[n-2];
            a[1]=b[n-1];
        }
    }
    m=n;
    if (n==2)
    {
        printf("%d",a[1]);
    }
    return 0;
}

#include <stdio.h> 

const int M = 3; 

int main() 
{ 
    int n, s = 0; 
    scanf("%d", &n); 
    for (int i = 2; i <= n; ++i) 
        s = (s+M)%i; 
    printf("%d\n", s+1); 
    return 0; 
}

#include <stdio.h>
#include<string.h>

int main ()
{
    int i,m,n;
    char str[100]={0};
    scanf("%d",&n);
    getchar();
    for(i=0;i<n;i++)
        str[i]=getchar();
    scanf("%d",&m);
    printf("%s\n",str+m-1);
}

#include <stdio.h>
#include <string.h>
#define N 80
void fun(char a[N],int m)
{char *p1,b[N];int i,l;
p1=a;i=1;l=0;
while(i<m){i++;p1++;}
while(*p1!='\0')
{b[l]=*p1;
 p1++;
 l++;
}
b[l]='\0';
puts(b);
}
int main()
{char a[N];int m,n;
scanf("%d\n",&n);
gets(a);
scanf("%d",&m);
fun(a,m);
return 0;
}

